import React, { useState } from 'react';
import MovieGenerator from './components/MovieGenerator';
import MovieList from './components/MovieList';
import './App.css';

const initialMovies = [
  { name: 'El Rey LeÃ³n', disabled: false },
  { name: 'Frozen', disabled: false },
  { name: 'Toy Story', disabled: false },
  { name: 'Up', disabled: false },
  { name: 'Coco', disabled: false }
];

function App() {
  const [movies, setMovies] = useState(initialMovies);

  const resetMovies = () => {
    setMovies(initialMovies);
  };

  const disableMovie = (index) => {
    setMovies(movies.map((movie, i) => (i === index ? { ...movie, disabled: true } : movie)));
  };

  return (
    <div className="App">
      <MovieGenerator onGenerate={resetMovies} />
      <h2>ðŸŽ¬ Lista de PelÃ­culas</h2>
      <MovieList movies={movies} onDisableMovie={disableMovie} />
    </div>
  );
}

export default App;
